import './Chessboard.css';
import Tile from '../Tile/Tile'

const N = 8;
const xAxis = ["a","b","c","d","e","f","g","h"];
const yAxis = ["1","2","3","4","5","6","7","8"];

interface Piece{
    image: string
    x: number
    y: number
}

const pieces:Piece[] = []
for(let i=0;i<8;i++){
    pieces.push({image:"assets/images/pawn_b.png",x:i , y:1})
}

for(let i=0;i<8;i++){
    pieces.push({image:"assets/images/pawn_b.png",x:i , y:4})
}

export default function Chessboard(){
    let board = [];

    for(let y=N;y>=0;y--){
        for(let x=0;x<N;x++){

            // check whether the sum of numbers x,y is even to draw each tile
            const isEven = (x+y+2)%2===0;
            let img = undefined;

            pieces.forEach(p=> {
                if(p.x===x && p.y===y){
                    img=p.image
                }
            })


            board.push(<Tile isEven={isEven} image={img}/>);
        }       
    }



    return <div id="chessboard">{board}</div>
}
