import { PieceType } from "../components/Chessboard/Chessboard";
import {TeamType} from "../components/Chessboard/Chessboard"
import {Piece} from "../components/Chessboard/Chessboard"



export default class Referee{
    tileIsOccupied(x: number,y: number, boardState: Piece[]):boolean{
        const piece = boardState.find(p=> p.x===x && p.y===y)
        return (piece===null)? true:false;
    }



    isValidMove(px:number, py:number, x:number, y:number, type:PieceType, team:TeamType, boardState:Piece[]){
        console.log(`referee checking.. piece: ${type}`);

        if(type === PieceType.PAWN){
            // our piece
            if(team === TeamType.OUR){
                // first movement 
                if(py===1){
                    // if does not change position in X axis
                    if(px===x && y-py===1){
                        if(!this.tileIsOccupied(x,y,boardState)){
                            return true;
                        }
                    } else if(px === x && y-py ===2){
                        if(!this.tileIsOccupied(x,y,boardState) && !this.tileIsOccupied(x,y-1,boardState)){
                        {
                            return true;
                            }
                        }
                    }
                } else{
                    // not the first movement
                    // allows movement of 1 posicion upwards
                    if(px===x && y - py ===1){
                        if(!this.tileIsOccupied(x,y,boardState)){
                        return true;
                        }
                    }
                }
            } else {
                // opponent's piece
                if(py===6){
                        if(px===x && (y-py===-1 || y-py ===-2)){
                            if(!this.tileIsOccupied(x,y,boardState)){
                                console.log("el pepe")
                                return true;
                        }
                    }
                }else{
                        if(px===x && y-py ===-1 ){
                            console.log("holalal");
                            return true;
                        }
                }
            }
        return false;
        }
    }
}
