import Chessboard from "../Chessboard/Chessboard";
import "./GameComps.css"
import LoginComponent from "../../Login/LoginComponent";
import { useMemo, useState } from "react";

function GameComps(){
    const [socket, setSocket] = useState<WebSocket | null>(null);
    const [status, setStatus] = useState<"offline" | "waiting" | "playing">("offline");
    const [roomId, setRoomId] = useState<string | null>(null);
    const [myColor, setMyColor] = useState<"white" | "black" | null>(null);
    const [joinCode, setJoinCode] = useState<string>("");

    function ensureSocket(): WebSocket{
        if(socket && (status === "waiting" || status === "playing")) return socket;
        const s = new WebSocket("ws://localhost:8080");
        s.onmessage = (ev: MessageEvent) => {
            try{
                const msg = JSON.parse(ev.data);
                if(msg.type === "waiting"){
                    setStatus("waiting");
                    setRoomId(msg.roomId);
                } else if(msg.type === "start"){
                    setRoomId(msg.roomId);
                    setMyColor(msg.color === "white" ? "white" : "black");
                    setStatus("playing");
                } else if(msg.type === "opponent_left"){
                    setStatus("offline");
                    setRoomId(null);
                    setMyColor(null);
                    try{ s.close(); } catch {}
                } else if(msg.type === "room_created"){
                    setStatus("waiting");
                    setRoomId(msg.roomId);
                } else if(msg.type === "error"){
                    alert(msg.error || "Error");
                }
            }catch(_e){
                // ignore malformed messages
            }
        };
        s.onclose = () => {
            setStatus("offline");
            setRoomId(null);
            setMyColor(null);
            setSocket(null);
        };
        setSocket(s);
        return s;
    }

    function createPrivateRoom(){
        const s = ensureSocket();
        if(s.readyState === WebSocket.OPEN){
            s.send(JSON.stringify({ type: 'create_room' }));
        } else {
            s.onopen = () => s.send(JSON.stringify({ type: 'create_room' }));
        }
    }

    function joinPrivateRoom(){
        const code = joinCode.trim();
        if(!code) return;
        const s = ensureSocket();
        const payload = JSON.stringify({ type: 'join_room', roomId: code });
        if(s.readyState === WebSocket.OPEN){
            s.send(payload);
        } else {
            s.onopen = () => s.send(payload);
        }
    }

    function disconnect(){
        try{ socket?.close(); } catch {}
        setStatus("offline");
        setRoomId(null);
        setMyColor(null);
        setSocket(null);
    }

    const online = useMemo(() => ({
        socket,
        status,
        roomId: roomId ?? undefined,
        myColor: myColor ?? undefined,
    }), [socket, status, roomId, myColor]);

    return <div id="component">
        <div id="online-controls">
            <button onClick={createPrivateRoom} disabled={status === "waiting" || status === "playing"}>Create Room (code)</button>
            <input
                placeholder="Enter room code"
                value={joinCode}
                onChange={(e)=> setJoinCode(e.target.value)}
                disabled={status === "waiting" || status === "playing"}
                style={{ marginLeft: 8 }}
            />
            <button onClick={joinPrivateRoom} disabled={status === "waiting" || status === "playing" || !joinCode.trim()}>Join Room</button>
            {status === "waiting" && <span className="hint">Waiting for opponent... Room: {roomId}</span>}
            {status === "playing" && <span className="hint">Room: {roomId} | You are {myColor}</span>}
            {(status === "waiting" || status === "playing") && <button onClick={disconnect}>Disconnect</button>}
        </div>
        <LoginComponent/>
        <Chessboard online={online}/>
    </div>
}

export default GameComps;
