import React, { useState, useRef, useEffect } from 'react';
import "./MusicStyle.css"
import musicFile from './music/me_equivoque_de_gemela.mp3';

const BackgroundMusic: React.FC = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef<HTMLAudioElement>(null);

  // Función para alternar reproducción
  const toggleMusic = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  // Iniciar reproducción automática (opcional)
  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = 0.3; // Volumen al 30%
    }
  }, []);

  return (
    <div className="bg-music-control">
      <audio 
        ref={audioRef} 
        loop 
        src={musicFile}
      />
      <button 
        onClick={toggleMusic} 
        className="musicButton"
      >
        {isPlaying ? 'Pausar Música' : 'Reproducir Música'}
      </button>
    </div>
  );
};

export default BackgroundMusic;
